function onCellEdit(fieldName, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	if (fieldName == 'supplier') {
		var gr = new GlideRecord('supplier');
		gr.addQuery('sys_id', newValue);
		gr.query();
		if (gr.next()) {
			g_form.setValue('Supplier.Street address', gr.getValue('street'));
		}
    }
  var saveAndClose = true;
 //Type appropriate comment here, and begin script below

 callback(saveAndClose); 
}
